export const reducer = (state, action) =>{
  switch (action.type) {
    case "ADD_VACCINE":
      const updateMember = [...state.newVaccine, action.payload];
      return{...state, newVaccine:updateMember}
    // break;
    default:
      return state;
  }
  }
