import React,{useState} from "react";

function App() {
  const [vaccineForm, setVaccineForm] = useState({name:'',date:'',vaccine:''})
  console.log(vaccineForm)
  const handleFormChange = (e) =>{
setVaccineForm({...vaccineForm,e})
  }
  return (
    <>
      <h1>User Reducer</h1>
      <form>
        <input onChange={handleFormChange(e)} type="text" name="name" placeholder="Name of candidate" /> <br /><br />
        <input onChange={handleFormChange(e)} type="date" name="date" placeholder="Vaccine Date" /> <br /><br />
        <input onChange={handleFormChange(e)} type="text" name="vaccine" placeholder="Vaccine" />
      </form>
    </>
  );
}

export default App;
